class MaxQueue {
private:
    queue qu;
    deque dq;
public:
    MaxQueue() {

    }

    int max_value() {
        if(qu.size()){
            return dq.front();
        }
        else{
            return -1;
        }
    }

    void push_back(int value) {
        qu.push(value);
        while(!dq.empty() &&value>dq.back()){
            dq.pop_back();
        }
        dq.push_back(value);
    }

    int pop_front() {
        if(!qu.size()) return -1;
        if(qu.front()==dq.front()){
            dq.pop_front();
        }
        int res = qu.front();
        qu.pop();
        return res;
    }
};

