最大矩形面积


class Solution {
public:
    int largestRectangleArea(vector& heights) {
        heights.push_back(0);
        stack stk;
        int ans=INT_MIN;
        for(int i = 0; i = heights[i]){
                int top = stk.top(); stk.pop();
                ans = max(ans,heights[top]*(stk.empty()?i:(i-stk.top()-1)));
            }
            stk.push(i);
        }
        return ans;
    }
};

