    TreeNode* rightReverse(TreeNode*root){
        TreeNode*cur=root->right;
        TreeNode*pre=root;
        pre->right=NULL;
        while(cur!=NULL){
            TreeNode*tmp=cur;
            cur=cur->right;
            tmp->right=pre;
            pre=tmp;
        }
