判断链表是否有环
bool hasCircle(ListNode* head){
    ListNode *slow, *fast;
    slow = head;
    fast = head;

    while(fast&&fast->next){
        slow = slow->next;
        fast = fast->next->next;
        if(slow == fast){
            return true;
        }
    }
    return false;
}

判断带环链表的入口节点
ListNode* findEnterNode(ListNode* head){
    ListNode *slow, *fast;
    slow = head;
    fast = head;

    while(fast&&fast->next){
        slow = slow->next;
        fast = fast->next->next;
        if(slow == fast){
            break;
        }
    }
    fast = head;
    while(slow != fast){
        slow = slow->next;
        fast = fast->next;
    }
    return slow;
}

